trigger.body:
 v_year VARCHAR2(10);
 v_value NUMBER;
 v_status VARCHAR2(10);
BEGIN

  IF AD_isTriggerEnabled()='N' THEN RETURN;
  END IF;

  IF (INSERTING OR UPDATING) THEN

--ValidaciÃ³n de Valor a Ajustar
	IF(:NEW.sfb_budget_item_id is null OR :NEW.sfb_budget_line_id is null)THEN
		RAISE_APPLICATION_ERROR(-20000, '@Sfbtr_BudgetLineError@');
	END IF;
	IF(:NEW.value_discounted=0)THEN
	      RAISE_APPLICATION_ERROR(-20000, '@Sfbtr_AdjustValue0@');
	ELSIF(:NEW.value_discounted<0)THEN
		RAISE_APPLICATION_ERROR(-20000, '@Sfbtr_ValueDiscountedNotAccepted@');
	ELSIF(:NEW.value_discounted>:NEW.available_value) THEN
			RAISE_APPLICATION_ERROR(-20000, '@Sfbtr_AdjustValueNotAccepted@');
	END IF;

  ELSIF (DELETING) THEN
	SELECT status INTO v_status 
	FROM sfbtr_budgetary_reforms  
	WHERE sfbtr_budgetary_reforms_id = :OLD.sfbtr_budgetary_reforms_id;
	IF (v_status = 'RE' OR v_status = 'AP')THEN
		RAISE_APPLICATION_ERROR(-20000, '@Sfbtr_AlreadyProcessedBudgetReforms@');
	END IF;
  END IF;

  END SFBTR_BUD_REFORMS_FROM_TRG
