trigger.body:
   V_MOVEMENT_ID VARCHAR(32); 	--OBTG:VARCHAR2--
   V_COUNT NUMBER;		--OBTG:NUMBER--

 
BEGIN
    
    
  IF AD_isTriggerEnabled()='N' THEN RETURN;
  END IF; 

  IF (DELETING) THEN

	V_MOVEMENT_ID:= :OLD.SFPR_EMPLOYEE_RVE_ID;

	
	IF V_MOVEMENT_ID IS NOT NULL THEN

		SELECT COUNT(*)
		INTO V_COUNT
		FROM SFPR_RVE_DETAIL
		WHERE SFPR_EMPLOYEE_RVE_ID = V_MOVEMENT_ID;

		IF V_COUNT > 0 THEN
			RAISE_APPLICATION_ERROR(-20000, '@Sfpr_ValidateRVEDetail@');
		END IF;
		
	END IF;	
	 	
  /*ELSIF (UPDATING) THEN

	V_MOVEMENT_ID:= :OLD.SFPR_EMPLOYEE_RVE_ID;

	
	IF V_MOVEMENT_ID IS NOT NULL THEN

		SELECT COUNT(*)
		INTO V_COUNT
		FROM SSPR_CONCEPT_AMOUNT CA
		LEFT JOIN SFPR_RVE_DETAIL RD ON RD.SFPR_RVE_DETAIL_ID = CA.EM_SFPR_RVE_DETAIL_ID 
		WHERE CA.EM_SFPR_RVE_DETAIL_ID  = V_MOVEMENT_ID;


		IF V_COUNT > 0 THEN
			RAISE_APPLICATION_ERROR(-20000, '@Sfpr_ValidateRVEDetail@');
		END IF;
		
	END IF;	*/
	 	
  END IF;
  

  END SFPR_ERVE_DETAIL_TRG
