trigger.body:
   V_SURROGATE_ID VARCHAR(32); 	--OBTG:VARCHAR2--
   V_PROCESSED VARCHAR(1);	--OBTG:VARCHAR2--
   V_STATUS VARCHAR(60);	--OBTG:VARCHAR2--
   V_COUNT NUMBER;		--OBTG:NUMBER--
   
BEGIN
    
 IF AD_isTriggerEnabled()='N' THEN RETURN;
  END IF; 

  IF (DELETING OR UPDATING) THEN

	V_SURROGATE_ID:= :OLD.SFPR_SURROGATE_DETAIL_ID;
	V_PROCESSED:= :OLD.PROCESSED;
	V_STATUS:= :OLD.STATUS_SURROGATE;
	
	IF V_SURROGATE_ID IS NOT NULL THEN

		IF V_PROCESSED = 'Y' AND V_STATUS = 'CO' THEN
		
			SELECT COALESCE(TO_NUMBER((SELECT COUNT(*) FROM SSPR_CONCEPT_AMOUNT
			WHERE EM_SFPR_SURROGATE_DETAIL_ID = V_SURROGATE_ID)),0) INTO V_COUNT FROM DUAL;

			IF (V_COUNT>0) THEN
			
				RAISE_APPLICATION_ERROR(-20000, '@Sfpr_ValidSurrogate@');
				
			END IF;
			
		END IF;
		
	END IF;	
	 	
  END IF;
  

  END SFPR_VALIDATE_SDETAIL_TRG
